spec.expr:
sort_desc(
  max by(id, name) (
    60 * grafanacloud_instance_samples_per_second{id=~"$metrics_id"}
    / grafanacloud_instance_active_series{id=~"$metrics_id"}

    * on(id) group_left(name) topk by (id) (1, grafanacloud_instance_info{id=~"$metrics_id"})
  )
)

sum(
  label_replace(
    label_replace(
      avg_over_time(grafanacloud_sm_billable_check_executions_per_second{org_id="$org_id"}[1h]),
      "class", "protocol", "check_class", "(protocol|scripted)"
    ), "class", "browser", "check_class", "browser"
  )
) by(class)

sum(
  label_replace(
    label_replace(
      sum_over_time((grafanacloud_sm_billable_check_executions_per_second{org_id="$org_id"} offset -1d * 60 )[1d:1m]),
      "class", "protocol", "check_class", "(protocol|scripted)"
    ), "class", "browser", "check_class", "browser"
  )
) by(class)

sort_desc(
  sum_over_time(
    sum by (id) (
      grafanacloud_logs_instance_query_bytes:rate5m{id=~"$logs_id"}
    ) [$__range:1m]
  ) /
  sum_over_time(
    sum by (id) (
      grafanacloud_logs_instance_billable_bytes_received_per_second{id=~"$logs_id"}
    ) [$__range:1m]
  )
) * on(id) group_left(name) topk by (id) (1, grafanacloud_logs_instance_info{id=~"$logs_id"})
